<style lang="scss">
  .switcher {
    border: 2px solid var(--white);
    background-color: var(--white);
    width: 68px;
    height: 34px;
    border-radius: 34px;
    display: block;
    box-sizing: border-box;
    margin: 0 auto;

    input[type="checkbox"] {
      display: none;

      &:checked {
        ~ .trigger {
          background-color: var(--maastricht-blue);
          transform: translateX(34px);
        }
      }
    }

    .trigger {
      background-color: var(--just-blue);
      border-radius: 50%;
      box-sizing: border-box;
      display: block;
      height: 30px;
      transition: transform var(--transition-time),
        background var(--transition-time);
      width: 30px;
    }
  }
</style>

<div>
  <label class="switcher">
    <input type="checkbox" on:change={changeMode} />
